import React, { Component } from 'react'
import Header from './Header'
import Show from './Show'
import Data from './Data'


import Footer from './Footer'
import ReactAudioPlayer from 'react-audio-player'




   class App  extends Component {
    constructor(){
      super()
      this.state = { Data : Data }
  }


  mapping(x){
    const stores = x.map( etoile =>
        <Show
            key={etoile.id}
            img={etoile.img}
            title={etoile.title}
            price={etoile.price}
            stock={etoile.stock}

        />);
    return stores
}


  render() {
    return (
      <div>
      <Modal.Body>{this.mapping(this.state.Data)}</Modal.Body>



        <Header />
        <Show/>




        <div className='music'> <ReactAudioPlayer
  src="ESSson.mp3"
  autoPlay
  controls
/></div>

        <Footer />






      </div>
    )
  }
}

export default App
