import React,{Component} from 'react'
import Slide from './Slide'

import Data from './Data'


class Store extends Component{
    constructor(){
        super()
        this.state = { Data : Data }
    }





    mapping(x){
            const stores = x.map( etoile =>
                <Slide
                    key={etoile.id}
                    img={etoile.img}
                    title={etoile.title}
                    price={etoile.price}
                    stock={etoile.stock}

                />);
            return stores
    }


    render(){
        return(
            <div className="row">

                {this.mapping(this.state.Data)}
                </div>
        )
    }
}

export default Store;
